import React, {PropTypes} from 'react';
import {Provider} from 'react-redux';

import store from './store';
import MeetWidget from './container';


export default function Root({onEvent, spark, shouldStartMeeting, toPersonId}) {
  return (
    <Provider store={store}>
      <MeetWidget
        onEvent={onEvent}
        shouldStartMeeting={shouldStartMeeting}
        spark={spark}
        toPersonId={toPersonId}
      />
    </Provider>
  );
}

Root.propTypes = {
  onEvent: PropTypes.func,
  shouldStartMeeting: PropTypes.bool,
  spark: PropTypes.object.isRequired,
  toPersonId: PropTypes.string.isRequired
};
