import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';

import Spinner from '@ciscospark/react-component-spinner';
import injectScrollable from '@ciscospark/react-hoc-scrollable';

import styles from './styles.css';

export function ScrollingActivity(props) {
  const {
    isLoadingHistoryUp
  } = props;
  let spinnerUp;

  const spinner = <div className={classNames(`ciscospark-spinner-container`, styles.spinnerContainer)}><Spinner /></div>;

  if (isLoadingHistoryUp) {
    spinnerUp = spinner;
  }

  return (
    <div>
      {spinnerUp}
      {props.children}
    </div>
  );
}

ScrollingActivity.propTypes = {
  children: PropTypes.node.isRequired,
  isLoadingHistoryUp: PropTypes.bool
};

export default injectScrollable(ScrollingActivity);
