import React from 'react';
import PropTypes from 'prop-types';
import {Provider} from 'react-redux';

import store from './store';
import MeetWidget from './container';


export default function Root({spark, shouldStartMeeting, toPersonId}) {
  return (
    <Provider store={store}>
      <MeetWidget
        shouldStartMeeting={shouldStartMeeting}
        spark={spark}
        toPersonId={toPersonId}
      />
    </Provider>
  );
}

Root.propTypes = {
  shouldStartMeeting: PropTypes.bool,
  spark: PropTypes.object.isRequired,
  toPersonId: PropTypes.string.isRequired
};
